mapper.select:
select

    distinct


from t_b_article a join t_si_user b on a.author_id=b.id




    order by ${orderByClause}



        limit ${offset}, ${limit}


        limit ${limit}

mapper.update:
update t_b_article a

    a.status=#{status},
    a.gmt_modified=#{modified}

where a.id=#{articleId}

mapper.select:
select DATE_FORMAT(gmt_create,'%Y') as year, DATE_FORMAT(gmt_create,'%m') as month, DATE_FORMAT(gmt_create,'%Y年%m月') as date, count(*) as count from t_b_article where type = 'post' and status = 'publish'
and DATE_FORMAT(gmt_create,'%Y')=#{arg0} and DATE_FORMAT(gmt_create,'%m')=#{arg1}
and categories like concat('%',#{arg2},'%') and tags like concat('%',#{arg3},'%') group by date order by date desc

mapper.update:
update t_b_article


        hits=#{hits},


        comments_num=#{commentsNum},


        likes=#{likes},


        dislikes=#{dislikes}


where id=#{articleId}
